/* ==UserStyle==
@name           Lapis-K
@namespace      https://github.com/Catgrills/Lapis-K
@version        1.0.0
@description    An userstyle for Pixiv called Lapis-K.
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #ECEFF1 !important; 
		--card-bg: #FFF !important;
		--card-bg-2: #F0F1F5 !important;
		--border-color: #DBDCE6 !important;
		--primary-color: #333 !important;
		--secondary-color: #7E7E7E !important;
        --invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #161925 !important; 
		--card-bg: #1D2D44 !important;
  		--card-bg-2: #152132 !important;
		--border-color: #0D1321 !important;
		--primary-color: #C0E0DE !important;
        --secondary-color: #AAABBC !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("pixiv.net") {
/*[[theme]]*/
/* GENERAL */

    /** DESCRIPTION : set the main font and change others elements for every page **/
    html {
        overflow-x: hidden !important;
    }

    body {
        background: var(--body-bg) !important;
        color: var(--primary-color);
    }

    a,
    .button,
    br,
    div,
    dt,
    input,
    li,
    span,
    strong,
    textarea,
    td,
    tr {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
        /* font-family: "Roboto", "落落の汤圆体 jojisin" !important; [CHINESE] */
        /* font-family: "Roboto", "BM YEONSUNG" !important; [KOREAN] */
    }

    #wrapper {
        width: 95% !important;
        margin-top: 100px !important;
    }

    #wrapper .layout-a {
        width: inherit !important;
    }

    .column-order-menu .pager-container {

        /*** PAGINATIONS ***/
        float: left !important;
        margin-top: 15px !important;
        margin-left: -50px !important;
    }

    .column-order-menu .pager-container .page-list li {
        background-color: var(--card-bg) !important;
        border: none;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 4px 1px 4px 0px !important;
        margin-right: 10px !important;
    }

    .column-order-menu .pager-container .page-list li.current {
        color: var(--primary-color) !important;
        padding: 4px 10px !important;
    }

    .column-order-menu .menu-items a {
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        text-transform: lowercase !important;
    }

    ._pager-complex li {
        background: var(--card-bg);
        border: none;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 4px 11px 4px 10px !important;
        margin-right: 10px !important;
    }

    .pages-current span {
        font-weight: normal !important;
    }

    ._pager-complex li:before {
        display: none !important;
    }

    ._pager-complex li:first-child,
    ._pager-complex li:last-child {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }

    ._pager-complex .pages-current span {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        line-height: 3px !important;
    }

    .prev ._button,
    .next ._button,
    ._pager-complex a[rel~="prev"],
    ._pager-complex a[rel~="next"],
    ._icon.sprites-prev-linked,
    ._icon.sprites-next-linked {
        display: none;
    }

    #back-to-top {
        filter: var(--invert) !important;
    }

    .ads_anchor,
    .ads_value .ads_inner,
    .booth-follow-items .event-name-badge,
    ._booth-promotion-container,
    .comic-hot-works,
    .content._mypage-fanbox,
	.followbooster-after-bookmark-caption,
    .footer,
	._icon._bookmark-icon-inline,
    .icon-pixpedia,
    .icon-pixpedia-no-item,
    ._icon.sprites-checked,
    ._icon.sprites-follow,
    ._icon.sprites-info.open-info.ui-modal-trigger,
    #item-container #user-recommend-module,
    .layout-column-2 #illust-recommend,
    ._layout-order-bookmark-wrapper,
    ._member-illust-medium-fanbox-area,
    ._member-page-fanbox-area,
    ._member-page-frequent-uploaded-work-tags-container,
    ._mute-edit-container .premium-banner,
    ._official-badge,
    .onecolumnBottom,
    #page-mypage .area_new,
    #page-mypage .booth-follow-items .information,
    #page-mypage .booth-follow-items .description,
    #page-mypage ._icon,
    #page-mypage #item-container h2,
    #page-mypage .more,
    #page-mypage ._pixivision-icon,
    #page-mypage ._pixivision-category-label.inspiration,
    #page-mypage .project_pixiv .mypage-period-badge,
    #page-mypage .velocity,
    .pixpedia-article-abstract,
    ._premium-lead-tag-search-bar,
    ._premium-lead-popular-d-body,
    ._profile-menu-unit ul.profile .premium-lead-premium-campaign,
    ._recommend-follow-for-visitor,
    .share-link-container,
    ._share-links.big-buttons,
    ._shortcut-help,
    .side-menu.comic-container,
    ._sketch-promotion-container .pixiv_sketch_tooltip,
    #toolbar-items,
    ._ui-tooltip[href*="booth"],
    .upload.ad-printservice,
    ._user-profile-card-badges,
    ._work-detail-unit .share-link-container,
    .worksOptionRightInfo a[href*="illust_infomsg"],
    .worksShare,
    #wrapper .layout-a .area_new.user-tags,
    ._2M38x92 {

        /*** USELESS ELEMENTS ***/
        display: none;
    }

    .column-header .pixpedia-link,
    ._more,
    ._user-profile-card ._premium-badge,
    .worksAlso {
        visibility: hidden;
    }

    .fbmvZrK,
    ._no-item {
        background: transparent;
        font-size: 15pt !important;
    }

    ._one-click-bookmark {
        background-image: url("https://i.imgur.com/63p1cf8.png") !important;
    }

    ._one-click-bookmark.on {
        background-image: url("https://i.imgur.com/wu2OcFV.pngg") !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header page **/
    ._global-header {

        /*** HEADER MAIN CONTAINER***/
        background-color: var(--card-bg) !important;
        border-top: 6px solid #369ECF;
        border-bottom: none;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.01);
        height: 120px !important;
    }

    ._global-header .layout-wrapper {

        /*** HEADER CONTENT ***/
        width: 90% !important;
    }

    ._global-header .title {

        /*** HEADER CONTENT LOGO ***/
        top: 30px !important;
    }

    .title.header-logo::after {

        /*** HEADER INSIDE CONTAINER TITLE LOGO ***/
        color: #369ECF !important;
        content: "お絵かきがもっと楽しくなる場所" !important;
        font-family: "KFhimaji" !important;
        font-size: 22pt !important;
        width: 30vw !important;
        position: absolute !important;
        margin-top: 5px !important;
        margin-left: 16px !important;
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
    }

    ._global-header .navigation-list {

        /*** HEADER CONTENT NAVIGATION LIST ***/
        right: 240px !important;
        left: inherit !important;
        bottom: 42px !important;
    }

    ._global-header .navigation-list .menus li a {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        opacity: .6 !important;
        text-transform: lowercase !important;
    }

    ._global-header .navigation-list .menus li > a.current {
        color: var(--primary-color) !important;    
    }

    ._global-header .navigation-list .home,
    ._global-header .navigation-list .menu-icon {
        display: none !important;
    }

    ._premium-lead-header,
    .header-novel.js-click-trackable-later,
    .header-r18.js-click-trackable-later,
    .header-help {

        /*** HEADER CONTENT LINKS ***/
        visibility: hidden !important;
    }

    .label.header-settings {
        background: no-repeat url("https://i.imgur.com/pLOzSFM.png") center !important;
        background-color: transparent !important;
        background-size: 20px auto !important;
        color: transparent;
        margin-top: 36px !important;
        margin-right: -40px !important;
        filter: var(--invert) !important;
        opacity: 0.3 !important;
        z-index: 999 !important;
    }

    ._global-header .navigation-menu-right {

        /*** HEADER CONTENT MENU RIGHT ***/
        display: none;
    }

    #suggest-container .container {

        /*** HEADER CONTENT SEARCH BAR ***/
        border: none !important;
        background: transparent !important;
    }

    #suggest-container #suggest-input {
        background: var(--card-bg-2) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .05) !important;
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 10px 30px 10px 10px !important;
        max-width: 100% !important;
        text-overflow: ellipsis !important;
        overflow: hidden !important;
        white-space: nowrap !important;
        margin-top: -8px !important;
    }

    #suggest-container .submit.sprites-search-old {
        background-color: transparent !important;
        margin-right: 8px !important;
        filter: brightness(0) var(--invert) !important;
        opacity: .2 !important;
    }

    #suggest-list {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 15px !important;
        margin-top: 10px !important;
        margin-left: 5px !important;
    }

    #suggest-list * {
        color: var(--primary-color) !important;
        font-family: "Roboto", "KFhimaji" !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        line-height: 16pt !important;
    }

    #suggest-list li:hover {
        background: transparent;
        color: var(--secondary-color) !important;
    }

    #suggest-list .search-category {
        display: none !important;
    }

    /* POPUP WINDOWS */

    /** DESCRIPTION : change the elements of the popup windows **/
    #notification-message,
    .notification-popup.new-version#notification-popboard {

        /*** POPUP WINDOWS NOTIFICATIONS ***/
        border-radius: 2px !important;
    }

    .notification-popup.new-version .header,
    ._3PHaofo {
        font-size: 12pt !important;
        font-weight: normal !important;
    }

    ._1UXofiI {
        margin-top: -3px !important;
    }

    ._3p8uP-p {
        font-size: 10pt !important;
        margin-left: 10px !important;
        margin-top: 7px !important;
    }

    #notification-message.new-version .name {
        font-family: "Roboto", "KFhimaji" !important;
        font-size: 12pt !important;
        font-weight: normal !important;
        height: 20px !important;
    }

    #notification-message.new-version .date {
        bottom: 10px !important;
    }

    .notification-popup.new-version .more a {
        font-size: 11pt !important;
        text-transform: lowercase !important;
    }

    ._global-header .link-list .settings-menu .items {

        /*** POPUP WINDOWS SETTINGS ***/
        background: var(--card-bg-2) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        padding: 5px !important;
        outline: none !important;
        margin-top: 5px !important;
        margin-right: -60px !important;
    }

    ._global-header .link-list .settings-menu .item {
        border-bottom: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding-bottom: 5px !important;
        margin-left: -5px !important;
        margin-right: -5px !important;
        margin-bottom: 6px !important;
    }

    ._global-header .navigation-list .menus li > a + a::before {
        border-color: var(--border-color)    
    }

    .mypixiv-message {

        /*** POPUP WINDOWS PIXIV MESSAGE ***/
        border-radius: 2px !important;
        border-top: 6px solid #2CC990 !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 5px 15px 15px 15px !important;
    }

    #modal-messages .modal-window div.mypixiv-message .header {
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        text-transform: lowercase !important;
    }

    #modal-messages .modal-window div.mypixiv-message .label {
        font-size: 12pt !important;
    }

    #modal-messages .modal-window div.mypixiv-message form textarea {
        background: var(--card-bg-2);
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-size: 12pt !important;
        max-width: 100% !important;
        min-width: 100% !important;
        min-height: 100px !important;
        margin-bottom: 20px;
    }

    #modal-messages .modal-window div.mypixiv-message form .buttons input,
    #modal-messages .modal-window div.mypixiv-message form .buttons .modal-cancel {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-family: "Roboto" !important;
        font-size: 12pt !important;
        font-weight: normal !important;
        height: 40px !important;
        line-height: 40px !important;
        width: 80px !important;
    }

    .SaRQDsr {

        /*** POPUP WINDOWS MUTE/SETTINGS ***/
        border-radius: 2px !important;
        border-top: 6px solid #2C82C9 !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .HpgJOxH {
        background: url('https://imgur.com/4GlrMN7.png') no-repeat 50% 50%;
        background-size: 25px auto !important;
        background-color: transparent !important;
        border: none !important;
        color: transparent !important;
        opacity: .6 !important;
    }

    .OwbPTs6 {
        visibility: hidden;
    }

    ._2akl2xD {
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        text-transform: lowercase !important;
    }

    .qzAmGoU .p7QJkNC,
    ._3VCwsE5 .-nASB33,
    ._3--uSq5 ._1DwJf6F {
        font-size: 12pt !important;
    }

    ._3VCwsE5 .-nASB33 {
        margin-top: 10px !important;
        margin-bottom: 10px !important;
    }

    ._mute-edit-container .lists .list .title,
    ._1TJHP4Y > h2,
    ._26pBIMd {
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        margin-left: 0px;
    }

    ._mute-edit-container .lists .list .item {
        margin-top: 20px !important;
    }

    ._mute-edit-container .lists .list .item:hover {
        background-color: transparent;
    }

    ._mute-edit-container ._user-icon {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        height: 50px !important;
        width: 50px !important;
    }

    ._mute-edit-container .lists .list .label {
        margin-left: 15px;
    }

    ._mute-edit-container .lists .list .label span {
        font-family: "Roboto", "KFhimaji" !important;
        font-size: 12pt !important;
    }

    ._mute-edit-container .lists .list ._action-button.mute,
    .aeFuj-U.aeF3Wnp,
    ._1i-Mwh7.aeF3Wnp {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-family: "Roboto" !important;
        font-size: 12pt !important;
        font-weight: normal !important;
    }

    ._2Mk5yZ8 ._1DwJf6F {
        display: none;
    }

    /* HOME */

    /** DESCRIPTION : change the elements of the home page **/
    #page-mypage {

        /*** HOME MAIN CONTAINER ***/
        padding-top: 30px !important;
        margin-left: 60px !important;
    }

    #page-mypage .profile,
    #page-bookmark-user-myself ._user-profile-card,
    #page-bookmark-reg-user ._user-profile-card {

        /*** HOME PROFILE BOX ***/
        background: var(--card-bg-2) !important;
        border: none !important;
        border-bottom: 1px solid var(--border-color) !important;
        border-radius: 1px !important;
        height: 90px !important;
        width: 100% !important;
        padding: 5px 82px !important;
        position: absolute !important;
        top: 126px !important;
        right: 0px !important;
        left: 0px !important;
    }

    #page-mypage .profile ._user-icon,
    #page-bookmark-user-myself ._user-profile-card ._user-icon,
    #page-bookmark-reg-user ._user-profile-card ._user-icon {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        height: 70px !important;
        width: 70px !important;
    }

    #page-bookmark-user-myself ._user-profile-card ._user-icon,
    #page-bookmark-reg-user ._user-profile-card ._user-icon {
        position: absolute !important;
        left: 7px !important;
        margin-top: 10px !important;
    }

    #page-mypage ._profile-menu-unit {
        border: none !important;
    }

    #page-mypage ._profile-menu-unit ul.profile .user-name-container {
        overflow: visible !important;
        margin-left: 30px !important;
    }

    #page-mypage ._profile-menu-unit ul.profile .user-name {
        color: var(--primary-color) !important;
        font-size: 25pt !important;
        font-stretch: condensed !important;
        overflow: visible !important;
        margin-top: -7px !important;
    }

    #page-bookmark-user-myself ._user-profile-card .user-name,
    #page-bookmark-reg-user ._user-profile-card .user-name {
        color: var(--primary-color) !important;
        font-size: 25pt !important;
        font-stretch: condensed !important;
        text-align: left !important;
        position: absolute !important;
        left: 97px !important;
        margin-top: 35px !important;
    }

    #page-mypage .menu-item {
        position: absolute !important;
        top: 130px !important;
        right: 0px !important;
    }

    #page-mypage ._profile-menu-unit .menu-item a {
        color: #FFF !important;
        padding: 0px !important;
    }

    #page-mypage ._profile-menu-unit .menu-item a:hover {
        background-color: transparent !important;
    }

    #page-mypage ._profile-menu-unit ul.menu-item a .count {
        color: #FFF !important;
        padding-left: 10px !important;
    }

    #page-mypage .menu-item li:nth-of-type(1) a {
        margin-top: -1px !important;
    }

    #page-mypage .menu-item li:nth-of-type(1) {
        background: #008891 !important;
        border-radius: 2px !important;
        border-top: none !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        height: 38px !important;
        padding: 0px 25px !important;
        width: 100px !important;
        text-transform: uppercase !important;
        position: absolute !important;
        top: 26px !important;
        right: 250px !important;
    }

    #page-mypage .menu-item li:nth-of-type(1) * {
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
        line-height: 39px !important;
    }

    #page-mypage .menu-item li:nth-of-type(2) * {
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
        line-height: 37px !important;
    }

    #page-mypage .menu-item li:nth-of-type(2) {
        background: #42729B;
        border-radius: 2px !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        padding: 0px 25px 0px 25px;
        height: 38px !important;
        width: 100px !important;
        position: absolute !important;
        top: 26px !important;
        right: 90px !important;
        text-transform: uppercase;
    }

    #page-mypage ._profile-menu-unit .menu-item:nth-child(3),
    #page-mypage ._profile-menu-unit .menu-item:nth-child(4),
    #page-mypage ._profile-menu-unit .menu-item:nth-child(5) {
        display: none !important;
    }

    #page-mypage #column-misc {
        visibility: hidden;
    }

    #page-mypage h1 a[href="/ranking.php?mode=daily"],
    #page-mypage a[href="/ranking.php?mode=original"] {
        border-radius: 2px !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        padding: 0px 25px;
        height: 38px !important;
        width: auto !important;
        position: absolute !important;
        top: 156px !important;
        text-transform: uppercase;
        overflow: hidden !important;
        text-align: center !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
        visibility: visible !important;
    }

    #page-mypage a[href="/ranking.php?mode=original"] {
        background: #D33257;
        width: 196px !important;
        right: 410px !important;
    }

    #page-mypage a[href="/ranking.php?mode=daily"] {
        background: #F29B34;
        width: 140px !important;
        right: 667px !important;
    }

    #page-mypage .rounded,
    #page-mypage #item-container,
    #page-mypage .item.daily {

        /*** HOME BOXES ***/
        background: transparent !important;
        border: none !important;
    }

    #page-mypage .ui-layout-east,
    #page-mypage .contents-east,
    #page-mypage .contents-main {
        float: none !important;
        width: 89vw !important;
    }

    #page-mypage .contents-main {
        position: absolute;
        left: 6vw !important;
        margin-top: 40px !important;
    }

    #page-mypage .NewsTop {
        display: none !important;
    }

    #page-mypage #item-container .item {
        background: var(--card-bg) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding: 14px 18px !important;
        margin-bottom: 30px !important;
        margin-left: -30px !important;
    }

    #page-mypage #item-container .item header h1 {
        background-color: transparent !important;
        border-bottom: 1px solid var(--border-color) !important;
        color: var(--secondary-color) !important;
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
